package com.gp.cong.logisoft.struts.action;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import com.gp.cong.logisoft.domain.CarriersOrLine;
import com.gp.cong.logisoft.hibernate.dao.CarriersOrLineDAO;
import com.gp.cong.logisoft.struts.form.CarrierCodeForm;

/** 
 * MyEclipse Struts
 * Creation date: 01-24-2008
 * 
 * XDoclet definition:
 * @struts.action path="/warehouseCode" name="warehouseCodeForm" input="/jsps/datareference/WarehouseCode.jsp" scope="request" validate="true"
 */
public class CarrierCodeAction extends Action {
    /*
     * Generated Methods
     */

    /**
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    public ActionForward execute(ActionMapping mapping, ActionForm form,
            HttpServletRequest request, HttpServletResponse response) throws Exception {
        CarrierCodeForm carrierCodeForm = (CarrierCodeForm) form;// TODO Auto-generated method stub
        String buttonValue = carrierCodeForm.getButtonValue();

        HttpSession session = ((HttpServletRequest) request).getSession();
        String carrierCode = carrierCodeForm.getCarrierCode();
        CarriersOrLineDAO carriersDAO = new CarriersOrLineDAO();

        if (buttonValue.equals("search")) {
            List chcode = carriersDAO.findCarrierCode(carrierCode);
            if (chcode != null) {
                if (chcode.size() > 0) {
                    String msg = "Please enter different Carrier Code, This Carrier Code already exists";
                    request.setAttribute("message", msg);
                    request.setAttribute("carriercode", "carriercode");
                } else {
                    CarriersOrLine carriers = new CarriersOrLine();
                    carriers.setCarriercode(carrierCode);
                    session.setAttribute("carriers", carriers);
                    if (session.getAttribute("eqptList") != null) {
                        session.removeAttribute("eqptList");
                    }
                    if (session.getAttribute("portLst") != null) {
                        session.removeAttribute("portLst");
                    }
                    request.setAttribute("carriercode", "addcarrier");

                }
            }
        }
        return mapping.findForward("carriercode");
    }
}