public class SearchUseCaseAction extends Action {
    /*
     * Generated Methods
     */

    /**
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    public ActionForward execute(ActionMapping mapping, ActionForm form,
            HttpServletRequest request, HttpServletResponse response) throws Exception{
        SearchUseCaseForm searchUseCaseForm = (SearchUseCaseForm) form;// TODO Auto-generated method stub
        HttpSession session = ((HttpServletRequest) request).getSession();
        String useCaseId = searchUseCaseForm.getUseCaseId();
        String useCaseDate = searchUseCaseForm.getTxtCal();
        String docSetKeyValue = searchUseCaseForm.getDocSetKeyValue();
        String flowFrom = searchUseCaseForm.getFlowFrom();
        String hours = searchUseCaseForm.getHours();
        String minutes = searchUseCaseForm.getMinutes();
        String time = useCaseDate + " " + hours + ":" + minutes;
        String status = searchUseCaseForm.getStatus();
        String buttonValue = searchUseCaseForm.getButtonValue();
        SimpleDateFormat sdf = new SimpleDateFormat("MM/dd/yyyy hh:mm");
        if (buttonValue.equals("searchall")) {
            request.setAttribute("buttonValue", buttonValue);
        } else {

            java.util.Date javaDate = null;
            if (time != null && !time.equals("")) {
                    javaDate = sdf.parse(time);
            }
            DataExchangeTransactionDAO dataExchangeTransactionDAO = new DataExchangeTransactionDAO();
            List useCaseList = dataExchangeTransactionDAO.findForUseCase(useCaseId, javaDate, docSetKeyValue, flowFrom, status);
            session.setAttribute("useCaseList", useCaseList);
            request.setAttribute("buttonValue", buttonValue);
        }

        return mapping.findForward("searchusecase");
    }
}