/** 
 * MyEclipse Struts
 * Creation date: 01-29-2008
 * 
 * XDoclet definition:
 * @struts.action path="/terminalCode" name="terminalCodeForm" input="/jsps/datareference/TerminalCode.jsp" scope="request" validate="true"
 */
public class TerminalCodeAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception {
		TerminalCodeForm terminalCodeForm = (TerminalCodeForm) form;// TODO Auto-generated method stub
		String buttonValue=terminalCodeForm.getButtonValue();
		HttpSession session = ((HttpServletRequest)request).getSession();
		String trmnum=terminalCodeForm.getTrmnum();
		RefTerminalDAO refTerminalDAO=new RefTerminalDAO();
		if(buttonValue.equals("search")){
                    List terminalNum= refTerminalDAO.findTermNumber(trmnum);
                    if(terminalNum!=null){
                        if(terminalNum.size()>0){
                            String msg="Please enter different Terminal Number, This Terminal Number already exists";
                            request.setAttribute("message",msg);
                        }else{
                            RefTerminal refTerminal=new RefTerminal();
                            refTerminal.setTrmnum(trmnum);
                            refTerminal.setFclExportIssuingTerminal("N");
                            session.setAttribute("terminal",refTerminal);
                            request.setAttribute("terminalcode", "addterminal");
                        }
                    }
		}
		return mapping.findForward("warehousecode");
	}
}
	