public class UserCodeAction extends Action {
    /*
     * Generated Methods
     */

    /**
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    public ActionForward execute(ActionMapping mapping, ActionForm form,
            HttpServletRequest request, HttpServletResponse response) throws Exception {
        UserCodeForm userCodeForm = (UserCodeForm) form;// TODO Auto-generated method stub
        String buttonValue = userCodeForm.getButtonValue();
        if (request.getParameter("btnvalue") != null &&
                !request.getParameter("btnvalue").trim().equals("")) {
            buttonValue = request.getParameter("btnvalue");
        }
        HttpSession session = ((HttpServletRequest) request).getSession();
        String loginName = userCodeForm.getLoginName();
        UserDAO userDAO = new UserDAO();
        if (buttonValue.equals("search")) {
            List usecode = userDAO.findLoginName(loginName);
            if (usecode != null) {
                if (usecode.size() > 0) {
                    String msg = "Please enter different Login Name, This Login Name already exists";
                    request.setAttribute("message", msg);
                    request.setAttribute("usercode", "usercode");
                } else {
                    User user = new User();


                    // warehouse.setAcCountryCode(genericCode);
                    // warehouse.setCountryCode(genericCode);
                    //warehouse.setId(warehouseCode);
                    user.setLoginName(loginName);

                    session.setAttribute("user", user);
                    request.setAttribute("usercode", "adduser");

                }
            }
        }
        request.setAttribute("palletList",  userDAO.getPalletList());
        request.setAttribute("packageList",  userDAO.getPackageList());
        return mapping.findForward("usercode");
    }
}