        String creditstatus = vendorForm.getCredit();
        String crlimit = vendorForm.getClimit();
        String crterms = vendorForm.getCterms();
        String bankacct = vendorForm.getBaccount();
        String deact = vendorForm.getDeactivate();
        String buttonValue = vendorForm.getButtonValue();
        vendorDomainObj.setLegalname(legalname);
        vendorDomainObj.setDba(dba);
        vendorDomainObj.setTin(tin);
