package com.gp.cong.logisoft.struts.action;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.gp.cong.logisoft.domain.Warehouse;
import com.gp.cong.logisoft.hibernate.dao.WarehouseDAO;
import com.gp.cong.logisoft.struts.form.WarehouseCodeForm;

/** 
 * MyEclipse Struts
 * Creation date: 01-24-2008
 * 
 * XDoclet definition:
 * @struts.action path="/warehouseCode" name="warehouseCodeForm" input="/jsps/datareference/WarehouseCode.jsp" scope="request" validate="true"
 */
public class WarehouseCodeAction extends Action { 
    /**
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    public ActionForward execute(ActionMapping mapping, ActionForm form,
            HttpServletRequest request, HttpServletResponse response) throws Exception {
        WarehouseCodeForm warehouseCodeForm = (WarehouseCodeForm) form;// TODO Auto-generated method stub
        String buttonValue = warehouseCodeForm.getButtonValue();
        HttpSession session = ((HttpServletRequest) request).getSession();
        String warehouseCode = warehouseCodeForm.getWarehouseCode();
        WarehouseDAO warehouseDAO = new WarehouseDAO();
        if (buttonValue.equals("search")) {
            List whcode = warehouseDAO.findWarehouseCode(warehouseCode);
            if (whcode != null) {
                if (whcode.size() > 0) {
                    String msg = "Please enter different Warehouse Code, This Warehouse Code already exists";
                    request.setAttribute("message", msg);
                    request.setAttribute("warehousecode", "warehousecode");
                } else {
                    if (session.getAttribute("warehouse") != null) {
                        session.removeAttribute("warehouse");
                    }
                    Warehouse warehouse = new Warehouse();
                    warehouse.setWarehouseNo(warehouseCode);
                    session.setAttribute("warehouse", warehouse);
                    request.setAttribute("warehousecode", "addwarehouse");

                }
            }
        }
        return mapping.findForward("warehousecode");
    }
}