package com.gp.cong.logisoft.struts.containermangement.action;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.gp.cong.logisoft.domain.Unit;
import com.gp.cong.logisoft.hibernate.dao.UnitDAO;
import com.gp.cong.logisoft.struts.containermangement.form.AddUnitpopupForm;

/** 
 * MyEclipse Struts
 * Creation date: 09-30-2008
 * 
 * XDoclet definition:
 * @struts.action path="/addUnitpopup" name="addUnitpopupForm" input="/jsps/Containermanagement/addUnitpopup.jsp" scope="request" validate="true"
 */
public class AddUnitpopupAction extends Action {
    /*
     * Generated Methods
     */

    /**
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    public ActionForward execute(ActionMapping mapping, ActionForm form,
            HttpServletRequest request, HttpServletResponse response) throws Exception {
        AddUnitpopupForm addUnitpopupForm = (AddUnitpopupForm) form;// TODO Auto-generated method stub
        String buttonValue = addUnitpopupForm.getButtonValue();
        HttpSession session = ((HttpServletRequest) request).getSession();
        String unitNo = addUnitpopupForm.getUnitNo();
        UnitDAO unitDAO = new UnitDAO();
        if (buttonValue.equals("search")) {
            List unitno = unitDAO.findUnitNo(unitNo);
            if (unitno != null) {
                if (unitno.size() > 0) {
                    String msg = "Please enter different Unit Number, This Unit Number already exists";
                    request.setAttribute("message", msg);
                    request.setAttribute("unitno", "unitno");
                } else {
                    if (session.getAttribute("warehouse") != null) {
                        session.removeAttribute("warehouse");
                    }
                    Unit unit = new Unit();
                    unit.setUnitNo(unitNo);
                    session.setAttribute("unit", unit);
                    request.setAttribute("unitno", "addunitno");

                }
            }
        }
        return mapping.findForward("unitno");
    }
}