package com.gp.cong.logisoft.utilities;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.gp.cong.logisoft.util.UtilitiesBC;
import com.lowagie.text.Utilities;

/** 
 * MyEclipse Struts
 * Creation date: 04-28-2010
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */
public class CommentsAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response)throws Exception {
		// TODO Auto-generated method stub
		CommentsForm commentsForm =(CommentsForm)form;
		String buttonValue=commentsForm.getButtonValue();
		UtilitiesBC utilitiesBC= new UtilitiesBC();
		if(buttonValue!=null){
			if(buttonValue.equalsIgnoreCase("Search")){
				setRequest(commentsForm, request);
			}
			else if(buttonValue.equalsIgnoreCase("deletCharges")){
				utilitiesBC.deleteCharges(commentsForm);
				setRequest(commentsForm, request);
			}
		}
		return mapping.getInputForward();
	}
	
	public void setRequest(CommentsForm commentsForm,HttpServletRequest request)throws Exception {
		UtilitiesBC utilitiesBC= new UtilitiesBC();
		request.setAttribute(UtilitiesBC.LIST, utilitiesBC.getComments(commentsForm));
		request.setAttribute(UtilitiesBC.COMMENTSFORM,commentsForm);
	}
}