import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.gp.cvst.logisoft.domain.AcctStructure;
import com.gp.cvst.logisoft.hibernate.dao.AcctStructureDAO;
import com.gp.cvst.logisoft.struts.form.AddStructurePopUpForm;

/** 
 * MyEclipse Struts
 * Creation date: 06-18-2008
 * 
 * XDoclet definition:
 * @struts.action path="/AddStructure" name="AddStructurePopUpForm" scope="request" validate="true"
 */
public class AddStructurePopUpAction extends Action {
    /*
     * Generated Methods
     */

    /**
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    public ActionForward execute(ActionMapping mapping, ActionForm form,
            HttpServletRequest request, HttpServletResponse response)throws Exception {
        String forwardName = "";
        String buttonValue = "";
        String index;
        AddStructurePopUpForm addstructureform = (AddStructurePopUpForm) form;// TODO Auto-generated method stub
        HttpSession session = ((HttpServletRequest) request).getSession();
        String structure = addstructureform.getStructure();
        String structuredesc = addstructureform.getStructuredesc();
        buttonValue = addstructureform.getButtonValue();
        AcctStructure accstrucdomain = null;
        AcctStructureDAO accstrDAO = new AcctStructureDAO();
        index = addstructureform.getIndex();
        List struclistforpopup = new ArrayList();
        if (buttonValue.equals("add")) {
            if (session.getAttribute("accstruc") == null) {
                accstrucdomain = new AcctStructure();
            } else {
                accstrucdomain = (AcctStructure) session.getAttribute("accstruc");
            }
            List accountStructureList = new ArrayList();
                Iterator itr = accstrDAO.getAcctStructureList();

                while (itr.hasNext()) {
                    Object[] row = (Object[]) itr.next();
                    String acctStructure = (String) row[1];
                    accountStructureList.add(acctStructure.trim());
                }

                accstrucdomain.setAcctStructureName(structure);
                accstrucdomain.setAcctStructureDesc(structuredesc);
                struclistforpopup.add(accstrucdomain);

            session.setAttribute("accstruc", accstrucdomain);

            if (accountStructureList.contains(structure)) {

                request.setAttribute("msg", "Account Structure already Exists");
            } else {

                accstrDAO.save(accstrucdomain);
                request.setAttribute("struclist", struclistforpopup);
            }

            forwardName = "success";
        }

        return mapping.findForward(forwardName);
    }
}